package com.org.gen.D2;

public class Pattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i = 1;i<=5;i++) {
	for(int j=1;j<=i;j++) {
		System.out.print("* ");
	}System.out.println();
}
for(int x= 4;x>=1;x--) {
	for(int y=1;y<=x;y++) {
		System.out.print("* ");
	}System.out.println();
}
	}

}
